import React, { useEffect, useState } from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import { bannerData } from '../../../data';
import './productSlider.css';
import Countdown from 'react-countdown';
import Button from '@mui/material/Button';

const ProductSlider = () => {
    const responsive = {
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 5,
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2,
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1,
        },
    };
    useEffect(() => {
        // Call the function to fetch products
        handleGetProducts();
    }, []);

    const [products, setProducts] = useState([]);
    const sbiimg =
        'https://rukminim2.flixcart.com/fk-p-flap/1000/90/image/606a19936b528d9d.jpg?q=20';

    const handleGetProducts = async () => {
        try {
            const response = await fetch('http://localhost:3000/products', {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const products = await response.json();
            setProducts(products);
            console.log('Products fetched successfully:', products);
        } catch (error) {
            console.error('There was a problem with the fetch request:', error);
        }
    };

    const timerURL =
        'https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/timer_a73398.svg';

    const renderer = ({ hours, minutes, seconds }) => {
        return (
            <div>
                {hours}:{minutes}:{seconds}
            </div>
        );
    };

    return (
        <div>
            <div
                style={{
                    display: 'flex',
                    alignContent: 'center',
                    height: '50px',
                }}
            >
                <p className="deal"> Deal of the day</p>
                <div style={{ display: 'flex', alignContent: 'center' }}>
                    <img src={timerURL} alt="" style={{ width: 24 }} />{' '}
                    <Countdown
                        style={{ margingTop: '20px' }}
                        date={Date.now() + 5.04e7}
                        renderer={renderer}
                    />
                    &nbsp;
                    <p>Left</p>
                </div>
                <Button variant="contained">View All</Button>
            </div>

            <Carousel
                className="carouselContainer"
                responsive={responsive}
                autoPlay={true}
                autoPlaySpeed={3000}
                infinite={true}
                swipeable={false}
                draggable={false}
                showDots={true}
                keyBoardControl={true}
                centerMode={true}
                containerClass="carousel-container"
                dotListClass="custom-dot-list-style"
                itemClass="carousel-item-padding-40-px"
            >
                {products.map((e, index) => (
                    <img
                        key={index} // Ensures unique keys for each element
                        src={e.url}
                        alt={`Banner ${index + 1}`} // Provides an alt attribute for accessibility
                        style={{ width: '100%', height: 'auto' }} // Ensures images are responsive
                    />
                ))}
            </Carousel>
        </div>
    );
};

export default ProductSlider;
