import CartDetail from './CartDetail';
import PriceCalculation from './PriceCalculation';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import PriceTag from '../BuyNow/PriceTag';
import LooksOneIcon from '@mui/icons-material/LooksOne';
import Button from '@mui/material/Button';
import BoltIcon from '@mui/icons-material/Bolt';
import Footerpage from '../Footer/Footerpage';

const Cart = () => {
    const [products, setProducts] = useState([]);
    const [showBox, setShowBox] = useState(1);
    const navigate = useNavigate();

    const [calculationPrice, setCalculationPrice] = useState({
        price: 0,
        discount: 0,
        totalPrice: '',
    });

    useEffect(() => {}, [products]);

    useEffect(() => {
        handleGetProducts();
    }, []);

    const handleShowBox = (id) => {
        setShowBox(id);
    };

    const handleGetProducts = async () => {
        try {
            const response = await fetch(`http://localhost:3000/find-cart`, {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const products = await response.json();
            setProducts(products);
            console.log('Products fetched successfully:', products);
        } catch (error) {
            console.error('There was a problem with the fetch request:', error);
        }
    };
    return (
        <>
            <div style={{ backgroundColor: 'whitesmoke' }}>
                <Row>
                    <Col lg={8} md={8} sm={6} xs={12} className="col">
                        <div className="content-container">
                            <div
                                id="header1"
                                className={`HeaderBox ${
                                    showBox === 1 ? 'active' : ''
                                }`}
                                onClick={() => handleShowBox(1)}
                            >
                                <LooksOneIcon id="1Icon" /> &nbsp; ORDER SUMMARY
                            </div>
                            {showBox === 1 && (
                                <div
                                    className="containerBox"
                                    style={{ marginTop: '-20px' }}
                                >
                                    {products.map((data) => (
                                        <>
                                            <CartDetail data={data} />
                                        </>
                                    ))}
                                </div>
                            )}

                            <div
                                style={{
                                    padding: '20px',
                                    backgroundColor: 'white',
                                    display: 'flex',
                                    justifyContent: 'flex-end',
                                }}
                            >
                                <Button
                                    onClick={() => {
                                        navigate(`/place-order`);
                                    }}
                                    style={{
                                        backgroundColor: '#fb641b',
                                        width: '300px',
                                        color: 'white',
                                        height: '55px',
                                        borderRadius: '2px',
                                    }}
                                >
                                    <BoltIcon />
                                    PLACE ORDER
                                </Button>
                            </div>
                        </div>
                    </Col>
                    <Col lg={4} md={4} sm={6} xs={12}>
                        <div className="price-container">
                            <PriceTag />
                        </div>
                    </Col>
                </Row>
                <Footerpage />
            </div>
        </>
    );
};

export default Cart;
