import './BuyNowContainer.css';
import React, { useState } from 'react';
import LoginSignup from './LoginSignup';
import DeliveryAddress from './DeliveryAddress';
import OrderSummary from './OrderSummary';
import PaymentOption from './PaymentOption';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import LooksOneIcon from '@mui/icons-material/LooksOne';
import LooksTwoIcon from '@mui/icons-material/LooksTwo';
import Looks3Icon from '@mui/icons-material/Looks3';
import Looks4Icon from '@mui/icons-material/Looks4';
import AddIcon from '@mui/icons-material/Add';
import PriceTag from './PriceTag';

const BuyNowContainer = (data) => {
    console.log('data===:', data);
    const [showBox, setShowBox] = useState(2);

    const handleShowBox = (id) => {
        setShowBox(id);
    };

    const [calculationPrice, setCalculationPrice] = useState({
        price: 0,
        discount: 0,
        totalPrice: '',
    });

    return (
        <div style={{ backgroundColor: 'whitesmoke' }}>
            <Row className="prj_rowone">
                <Col lg={8} md={8} xs={12} className="col">
                    <div className="content-container">
                        <div
                            id="header1"
                            className={`HeaderBox ${
                                showBox === 1 ? 'active' : ''
                            }`}
                            onClick={() => handleShowBox(1)}
                        >
                            <LooksOneIcon id="1Icon" /> &nbsp; LOGIN OR SIGNUP
                        </div>
                        {showBox === 1 && (
                            <div className="containerBox">
                                <LoginSignup />
                            </div>
                        )}

                        <div
                            style={{ display: 'flex' }}
                            id="header2"
                            className={`HeaderBox centerBox ${
                                showBox === 2 ? 'active' : ''
                            }`}
                            onClick={() => handleShowBox(2)}
                        >
                            <div>
                                <LooksTwoIcon id="2Icon" />
                                &nbsp; DELIVERY ADDRESS
                            </div>
                            <div style={{ paddingRight: '20px' }}>
                                <AddIcon style={{ color: 'blue' }} />
                                &nbsp; Add a new address
                            </div>
                        </div>
                        {showBox === 2 && (
                            <div className="containerBox">
                                <DeliveryAddress />
                            </div>
                        )}

                        <div
                            id="header3"
                            className={`HeaderBox ${
                                showBox === 3 ? 'active' : ''
                            }`}
                            onClick={() => handleShowBox(3)}
                        >
                            <span>
                                <Looks3Icon id="3Icon" />
                            </span>
                            &nbsp; ORDER SUMMARY
                        </div>
                        {showBox === 3 && (
                            <div className="containerBox">
                                <OrderSummary />
                            </div>
                        )}

                        <div
                            id="header4"
                            className={`HeaderBox ${
                                showBox === 4 ? 'active' : ''
                            }`}
                            onClick={() => handleShowBox(4)}
                        >
                            <Looks4Icon id="4Icon" />
                            &nbsp; PAYMENT OPTIONS
                        </div>
                        {showBox === 4 && (
                            <div className="containerBox">
                                <PaymentOption />
                            </div>
                        )}
                    </div>
                </Col>
                <Col lg={4} md={4} xs={12} className="col">
                    <div className="price-container">
                        <PriceTag calculationPrice={calculationPrice} />
                    </div>
                </Col>
            </Row>
        </div>
    );
};

export default BuyNowContainer;
